import React,{useState} from "react";
import {Button, Container, Nav, Navbar} from "react-bootstrap"
import Products from "./Components/Products";
import './App.css'
import Cart from "./Components/Cart/Cart";
import ContextProvider from "./Components/Store/ContextProvider";


const App=()=>{

  const [ShowCart,setShowCart]=useState(false)

  const StateChangeHandler=()=>{
    setShowCart(!ShowCart)
  }
  return(
    <ContextProvider>
      <Navbar bg="dark" expand="sm" variant="dark">
        <Container>
          <header className="header">Home</header>
          <header className="header">Store</header>
          <header className="header">About</header>
          <Button variant="dark" onClick={StateChangeHandler} style={{borderColor: "blue"}}>Cart</Button>
        </Container>
      </Navbar>
      <div >
      <Navbar bg="secondary" variant="dark" className="mb-3" >
        <Container style={{width: "20px", height: "15rem"}}>
          <h2 className="center-navbar">The_Generics</h2>
        </Container>
      </Navbar>
      </div>
      {ShowCart && <Cart />}
      <p style={{fontFamily: "cursive", fontSize: "50px", textAlign: "center"}}>Music</p>
      <Products />
    </ContextProvider>
  )
}

export default App
