import React,{useContext,useEffect} from "react";
import CartItems from "./CartItems";
import './Cart.css'
import Context from "../Store/context";
import { Modal, ModalHeader, ModalTitle } from "react-bootstrap";



const Cart=(props)=>{

    const Cartctx=useContext(Context)

    let totalAmount=0
    for(let i=0;i<Cartctx.items.length;i++){
        totalAmount=totalAmount+ ((Cartctx.items[i].price)*(Cartctx.items[i].amount))
    }

    const cartitems=<ul>{Cartctx.items.map((product)=>
            <CartItems 
               key={product.id}
               url={product.imageUrl}
               title={product.title}
               price={product.price}
               amount={product.amount}
            />
        )}</ul>
    return(
        <Modal backdrop="static">
            <ModalHeader closeButton>
                <ModalTitle>Cart</ModalTitle>
            </ModalHeader>
        </Modal>
    )
}

export default Cart
