import React,{useContext} from "react";
import CartItems from "./CartItems";
import './Cart.css'
import Context from "../Store/context";



const Cart=()=>{
    const productsArr = [
        { title: 'Album 1', price: 100,imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%201.png'},
        { title: 'Album 2', price: 50, imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%202.png' },
        { title: 'Album 3',price: 70, imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%203.png' },
        { title: 'Album 4', price: 100, imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%204.png'}]

        const Cartctx=useContext(Context)

        const cartitems=<ul>{Cartctx.items.map((product)=>
            <CartItems 
               key={Math.random()}
               url={product.imageUrl}
               title={product.title}
               price={product.price} />
        )}</ul>
    return(
        <div className="cart">
            <p style={{fontFamily: "cursive", textAlign: "center"}}>Cart</p>
            {cartitems}
        </div>
    )
}

export default Cart
