import React from "react";
import { Card,Row,Col } from 'react-bootstrap'
import classes from './ExpenseDetails.module.css'

const ExpenseDetails= (props)=>{

    const DeleteHandler= async (id)=>{

        try{

            const response= await fetch(`https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses/${id}.json`,{
            method: 'DELETE'
            })

            if(!response.ok){
                throw new Error('Couldnt dete from database')
            }
        }catch (err){
            alert(err.message)
        }
    }

    
    
    
    return(
        <Card className={classes.users}>
        <Card.Title>{props.id}</Card.Title>
        <Card.Body>
            <Row>
                <Col>{props.category}</Col>
                <Col>{props.price}</Col>
                <Col>{props.description}</Col>
                <Col>
                  <button onClick={DeleteHandler.bind(null,props.id)} >Delete</button> 
                </Col>
            </Row>
        </Card.Body>
        </Card>
    )
}

export default ExpenseDetails
