import { useState, useRef,useContext } from 'react';
import classes from './Login.module.css'
import { useNavigate } from 'react-router-dom'

const Login = () => {
  
  const history=useNavigate()
  const authCtx=useContext(AuthContext)
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading,setIsLoading]=useState(false)
  const EmailRef=useRef()
  const PasswordRef=useRef()
  const ConfPasswordRef=useRef()

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
    
  };

  const SubmitHandler= (event)=>{

    event.preventDefault()

    let errorMessage='Authentication Failed!'
    setIsLoading(true)
    const enteredEmail=EmailRef.current.value;
    const enteredPass= PasswordRef.current.value;
    const enteredConf= ConfPasswordRef.current.value;

    if(enteredPass!=enteredConf){
        alert('Please Check the password!!')
    }else{
        let url;
     if(isLogin){
      url='https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY'
     }else{
       url='https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY'
     
     }
      fetch(url,{
        method: 'POST',
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPass,
          returnSecureToken: true
        }),
        headers: {'Content-Type': 'application/json'}
      })
      .then((response)=>{
        if(response.ok){
          return response.json()
        }else{
           return response.json().then(data=>{
            if(data && data.error && data.error.message){
              errorMessage=data.error.message
              throw new Error(errorMessage)
            }
          })
        }})
      .then((data)=>{
        authCtx.Login(data.idToken);
        history('/');
      }).catch((err)=>{alert(err.message)})
      
      EmailRef.current.value=''
      PasswordRef.current.value='' 
      ConfPasswordRef.current.value=''
    setIsLoading(false)
    }
    
  }

  let content=<button type='submit'>{isLogin ? 'Login' : 'Create an account'}</button>

  if(isLoading){
    content=<p>Sending Request...</p>
  }

  return (
    
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form  onSubmit={SubmitHandler}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' required ref={EmailRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input
            type='password'
            id='password'
            required
            ref={PasswordRef}
          />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Confirm Your Password</label>
          <input
            type='Confpassword'
            id='Confpassword'
            required
            ref={ConfPasswordRef}
          />
        </div>
        <div className={classes.actions}>
          {content}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default Login;
