import React from "react";
import { Card } from 'react-bootstrap'
import classes from './ExpenseDetails.module.css'

const ExpenseDetails= (props)=>{

    const DeleteHandler= async (id)=>{

        try{

            const response= await fetch(`https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses/${id}.json`,{
            method: 'DELETE'
            })

            if(!response.ok){
                throw new Error('Couldnt dete from database')
            }
        }catch (err){
            alert(err.message)
        }
    }
    
    
    return(
        <Card className={classes.users}>
        <ul>
            <li key={props.key}>
                {props.category}-{props.description}-{props.price}:
                <button onClick={DeleteHandler.bind(null,props.key)} >Delete</button> 
            </li>
        </ul>
        </Card>
    )
}

export default ExpenseDetails
