import React, { useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import classes from './AddExpenseForm.module.css'
import ExpenseDetails from "./ExpenseDetails";

const AddExpenseForm = () => {

  const [des, setDes] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('Entertainment');
  const [expenses, setExpenses] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editingExpenseId, setEditingExpenseId] = useState(null);

  useEffect(() => {
    fetchExpenses();
  }, []);

  const DesChangeHandler = (event) => {
    setDes(event.target.value);
  }

  const PriceChangeHandler = (event) => {
    setPrice(event.target.value);
  }

  const CategoryChangeHandler = (event) => {
    setCategory(event.target.value);
  }

  const updateExpense = async (expenseId) => {
    try {
      const response = await fetch(`https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses/${expenseId}.json`, {
        method: 'PUT',
        body: JSON.stringify({
          description: des,
          price: price,
          category: category
        }),
        headers: { 'Content-Type': 'application/json' }
      });
      if (response.ok) {
        alert('Expense has been updated');
        // Fetch the updated expenses after an existing one has been edited
        fetchExpenses();
      } else {
        throw new Error('Could not update the expense');
      }
    } catch (err) {
      alert(err.message);
    }
  }

  const handleEditExpense = (expenseId) => {
    
    const editedExpense = expenses.find(expense => expense.id === expenseId);
    if (editedExpense) {
      setDes(editedExpense.description);
      setPrice(editedExpense.price);
      setCategory(editedExpense.category);
      setIsEditing(true);
      setEditingExpenseId(expenseId);
    }
  }

  const fetchExpenses = async () => {
    try {
      const response = await fetch('https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses.json');
      if (response.ok) {
        const data = await response.json();
        const loadedExpenses = [];
        for (const key in data) {
          loadedExpenses.push({
            id: key,
            description: data[key].description,
            price: data[key].price,
            category: data[key].category
          });
        }
        setExpenses(loadedExpenses);
      } else {
        throw new Error('Failed to fetch expenses');
      }
    } catch (err) {
      alert(err.message);
    }
  }

  const SubmitNewExpense=async ()=>{
    try {
      const response = await fetch('https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses.json', {
        method: 'POST',
        body: JSON.stringify({
          description: des,
          price: price,
          category: category
        }),
        headers: { 'Content-Type': 'application/json' }
      });
      if (response.ok) {
        alert('Expense has been updated');
        // Fetch the updated expenses after a new one has been added
        fetchExpenses();
      } else {
        throw new Error('Could not store the expense');
      }
    } catch (err) {
      alert(err.message);
    }
  }

  const SubmitHandler = async (event) => {
    event.preventDefault();

    if (des.trim() === '' || price.trim() === '' || category.trim() === '') {
      alert('Please enter all fields to add a new expense.');
      return;
    }
  
    if (isEditing) {
      // Call the updateExpense function with the expense ID to update the existing expense
      updateExpense(editingExpenseId);
    } else {
      // Call the SubmitNewExpense function to add a new expense
      SubmitNewExpense();
    }

    setPrice('');
    setCategory('Entertainment');
    setDes('');
  }

  return (
    <Card className={classes.input}>
      <Card.Title style={{ textAlign: 'center', fontFamily: 'monospace', fontSize: '25px' }}>Add Daily Expense</Card.Title>
      <Card.Body>
        <form onSubmit={SubmitHandler}>
          <div>
            <label htmlFor="name">Description:</label>
            <input id="name" type="text" value={des} onChange={DesChangeHandler} />
          </div>
          <div>
            <label htmlFor="number">Money Spent:</label>
            <input id="number" type="number" value={price} onChange={PriceChangeHandler} />
          </div>
          <div>
            <label htmlFor="category">Category:</label>
            <select name="category" id="category" onChange={CategoryChangeHandler}>
              <option value="Entertainment">Entertainment</option>
              <option value="Food">Food</option>
              <option value="Petrol">Petrol</option>
            </select>
          </div>
          <br />
          <div>
            <button className={classes.button} type="submit">Add Expense</button>
          </div>
        </form>
      </Card.Body>
      <br />
      <ul>
        {expenses.map((expense) => (
          <ExpenseDetails
            key={expense.id}
            id={expense.id}
            description={expense.description}
            price={expense.price}
            category={expense.category}
            onEdit={handleEditExpense.bind(null,expense.id)}
          />
        ))}
      </ul>
    </Card>
  )
}

export default AddExpenseForm;
