import React,{useContext, useState} from "react";
import {Container} from 'react-bootstrap'
import LoginContext from "../Store/login-context";


const Profile=()=>{

    const [enteredName,setEnteredName]=useState('')
    const [enteredURL,setEnteredURL]=useState('')
    const ctx=useContext(LoginContext)

    const NameChangeHandler=(event)=>{
        setEnteredName(event.target.value)
    }

    const URLChangeHandler=(event)=>{
        setEnteredURL(event.target.value)
    }

    const SubmitHandler= async (event)=>{

        event.preventDefault()

        try{
           const response= await  fetch('https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY',{
            method: 'POST',
            body: JSON.stringify({
                idToken: ctx.idToken,
                displayName: enteredName,
                photoUrl: enteredURL,
                returnSecureToken: true
            }),
            headers: {'Content-Type': 'application/json'}
        })
        if(response.ok){
            alert('Your details have been updated')
        }
        else{
            throw new Error('Something went wrong here!');
        }

         const res= await fetch('https://identitytoolkit.googleapis.com/v1/accounts:lookup?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY',{
            method: 'POST',
            body: JSON.stringify({idToken: ctx.idToken}),
            headers: {'Content-Type': 'application/json'}
         })
         if(!res.ok){
            throw new Error('Something went wrong!');
         }

         const data = await res.json()
         setEnteredName(data.users[0].displayName)
         setEnteredURL(data.users[0].photoUrl)
         
         
        
        }catch(err) {
            alert(err.message)
        }
        
    }

    const EditDetailsHandler=()=>{
        setEnteredName('')
        setEnteredURL('')
    }

    return(
        <Container>
            <form onSubmit={SubmitHandler} style={{backgroundColor: 'gray'}}>
            <header>
                <h2 style={{textAlign: 'center'}}>Contact Details</h2>
                <div style={{textAlign: 'end'}}>
                    <button onClick={EditDetailsHandler}>Edit details</button>
                </div>
            </header>
            <main>
                <label>Full Name:</label>
                {enteredName && <input type="text" value={enteredName} onChange={NameChangeHandler} />}
                {!enteredName && <p>{enteredName}</p>}
                <br />
                <label>Profile Photo URL:</label>
                <input type="url" value={enteredURL} onChange={URLChangeHandler} />
            </main>
            <footer>
                <button type="submit">Update</button>
            </footer>
            </form>
        </Container>
    )
}

export default Profile
