import React,{useContext, useState} from "react";
import {Container} from 'react-bootstrap'
import LoginContext from "../Store/login-context";
import { json } from "react-router-dom";

const Profile=()=>{

    const [enteredName,setEnteredName]=useState('')
    const [enteredURL,setEnteredURL]=useState('')
    const ctx=useContext(LoginContext)

    const NameChangeHandler=(event)=>{
        setEnteredName(event.target.value)
    }

    const URLChangeHandler=(event)=>{
        setEnteredURL(event.target.value)
    }

    const SubmitHandler= async (event)=>{

        event.preventDefault()

        try{
           const response= await  fetch(`https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/${ctx.idToken}.json`,{
            method: 'POST',
            body: JSON.stringify({
                idToken: ctx.idToken,
                displayName: enteredName,
                photoUrl: enteredURL,
                returnSecureToken: true
            }),
            headers: {'Content-Type': 'application/json'}
        })
        if(response.ok){
            alert('Your details have been updated')
        }
        else{
            throw new Error('Something went wrong here!');
        }

         const res= await fetch(`https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/${ctx.isLoggedin} `)
         if(!res.ok){
            throw new Error('Something went wrong!');
         }

         const data=res.json()
         for (const key of data){
            setEnteredName(data[key].displayName)
            setEnteredURL(data[key].photoUrl)
            console.log(key)
         }
        
        }catch(err) {
            alert(err.message)
        }
        
    }

    return(
        <Container>
            <form onSubmit={SubmitHandler} style={{backgroundColor: 'gray'}}>
            <header>
                <h2>Contact Details</h2>
            </header>
            <main>
                <label>Full Name:</label>
                <input type="text" value={enteredName} onChange={NameChangeHandler} />
                <br />
                <label>Profile Photo URL:</label>
                <input type="url" value={enteredURL} onChange={URLChangeHandler} />
            </main>
            <footer>
                <button type="submit">Update</button>
            </footer>
            </form>
        </Container>
    )
}

export default Profile
