import React, { useContext } from "react";
import { NavLink } from "react-router-dom";
import LoginContext from "../Login/Store/login-context";
import { Button } from "react-bootstrap";

const Home=()=>{

    const ctx= useContext(LoginContext)

    const VerifyHandler=()=>{

        fetch('https://identitytoolkit.googleapis.com/v1/accounts:sendOobCode?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY',{
            method: 'POST',
            body: {
                requestType: 'VERIFY_EMAIL',
                idToken: ctx.idToken
            },
            headers: { 'Content-Type' : 'application/json' }
        })
    }

    return(
        <React.Fragment>
            <h1 style={{textAlign: 'center'}}>Welcome to expense tracker!!</h1>
            {ctx.isLoggedin && <p style={{textAlign: 'end'}}>
                Your profile is incomplete <NavLink to='/Profile'>Complete Now</NavLink>
            </p>}
            {ctx.isLoggedin && <p style={{textAlign: 'end'}}>
                <Button onClick={VerifyHandler} variant="Primary"> Verify your E-mail</Button>
            </p>}
            <br />
            <p>This is Expense Tracker</p>
        </React.Fragment>    
    )
}


export default Home
