import React, { useState } from "react";
import { Card } from "react-bootstrap";
import classes from './AddExpenseForm.module.css'

const AddExpenseForm=(props)=>{

    const [des,setDes]=useState('')
    const [price,setPrice]=useState('')
    const [category,setCategory]=useState('Entertainment')

    

    const DesChangeHandler=(event)=>{
        setDes(event.target.value)
    }

    const PriceChangeHandler=(event)=>{
        setPrice(event.target.value)
    }

    const CategoryChangeHandler=(event)=>{
        setCategory(event.target.value)
    }

    const SubmitHandler= async (event)=>{

        event.preventDefault()
        try{
           const response= fetch('https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses.json',{
            method: 'POST',
            body: JSON.stringify({
                description: props.expense.description,
                price: props.expense.price,
                category: props.expense.category 
            }),
            headers: {'Content-Type': 'application/json'}
           })
           if(response.ok){
            alert('Expense has been updated')
            }   else{
                throw new Error('Couldnt store the expense')
            }
        }catch(err){
            alert(err.message)
        }

        try{
            let errorMessage='Authentication Failed!'
            let data;
            const response=fetch('https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses.json')
            if(response.ok){
                console.log(response.json())
                data= response.json()
            }else{
                data = await response.json();
                if (data && data.error && data.error.message) {
                    errorMessage = data.error.message;
                    throw new Error(errorMessage);
                }
            }
            let Details=[]
            for(const key of data){
            Details.push({
                description: data[key].description,
                price: data[key].price,
                category: data[key].category
            })
            props.onAdd(Details)
            }
      
        }catch(err){
            alert(err.message)
        }
        setPrice('')
        setCategory('Entertainment')
        setDes('')
        
         
    }

    return(
         <Card className={classes.input}>
            <Card.Title style={{textAlign: 'center', fontFamily: 'monospace', fontSize: '25px'}}>Add Daily Expense</Card.Title>
            <Card.Body>
            <form onSubmit={SubmitHandler}>
            <div>
                <label htmlFor="name">Description:</label>
                <input id="name" type="text" value={des} onChange={DesChangeHandler}  />
            </div>
            <div>
                <label htmlFor="number">Money Spent:</label>
                <input id="number" type="number" value={price} onChange={PriceChangeHandler}   />
            </div>
            <div>
            <label htmlFor="category">Category:</label>
                <select name="category" id="category" onChange={CategoryChangeHandler}>
                    <option value="Entertainment">Entertainment</option>
                    <option value="Food">Food</option>
                    <option value="Petrol" >Petrol</option>
                </select>
            </div>
            <br />
            <div>
                <button className={classes.button} type="submit">Add Expense</button>
            </div>
          </form>
            </Card.Body>
         </Card>
    )
}

export default AddExpenseForm
