import {configureStore, createSlice} from '@reduxjs/toolkit'



const initialState = {
    // AuthState
    [userId]: {
      isLoggedin: localStorage.getItem('token'),
      idToken: '',
    },
    // ExpenseState
    [userId]: {
      description: '',
      price: 0,
      category: '',
      activatePremium: false,
    },
  };
let x=0

const AuthSlice = createSlice({
    name: 'Auth',
    initialState: initialState.AuthState,
    reducers: {
      login(state, action) {
        const { userId } = action.payload;
        localStorage.setItem('token', action.payload.token);
        state[userId].isLoggedin = true;
        state[userId].idToken = action.payload.token;
      },
      logout(state, action) {
        const { userId } = action.payload;
        localStorage.removeItem('token');
        state[userId].isLoggedin = false;
        state[userId].idToken = undefined;
      },
    },
  });
  

const ExpenseSlice=createSlice({
    name: 'Expense',
    initialState: ExpenseState,
    reducers: {
        description (state,action){
            state.description=action.payload
        },
        price(state,action){
            state.price=action.payload
            x+=state.price
            if(x>10000){
                state.activatePremium=true
            }else{
                state.activatePremium=false
            }
        },
        category(state,action){
            state.category=action.payload
        }
    }
})

const store= configureStore({
    reducer: {Auth: AuthSlice.reducer, Expense: ExpenseSlice.reducer}
})

export const AuthActions=AuthSlice.actions
export const ExpenseActions=ExpenseSlice.actions

export default store
