import { useState, useRef,useContext } from 'react';
import classes from './Login.module.css'
import { useNavigate } from 'react-router-dom'
import LoginContext from './Store/login-context';

const Login = () => {
  
  const navigate=useNavigate()
  const ctx=useContext(LoginContext)
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading,setIsLoading]=useState(false)
  const EmailRef=useRef()
  const PasswordRef=useRef()
  const ConfPasswordRef=useRef()

  const switchLoginHandler=()=>{
    setIsLogin((prevState) => !prevState);
  }

  const SubmitHandler= (event)=>{

    event.preventDefault()

    let errorMessage='Authentication Failed!'
    setIsLoading(true)
    const enteredEmail=EmailRef.current.value;
    const enteredPass= PasswordRef.current.value;
  
    
       
    
    let url;
    if(isLogin){
      url='https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY'
      console.log('2',url)
     }else{
       url='https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY'
       
     }
      
     //if(isLogin || enteredConf===enteredPass){
      console.log('1')
      fetch(url,{
        method: 'POST',
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPass,
          returnSecureToken: true
        }),
        headers: {'Content-Type': 'application/json'}
      })
      .then(async (response)=>{
        console.log('3')
        if(response.ok){
          console.log('4')
          return response.json()
        }else{
           console.log('5')
           const data = await response.json();
           if (data && data.error && data.error.message) {
            errorMessage = data.error.message;
            throw new Error(errorMessage);
          }
        }})
      .then((data)=>{
        console.log('6')
        if(isLogin){
          console.log('7')
          ctx.login(data.idToken,enteredEmail)
          navigate('/Home');
        }else{
          alert('Your account has been created!')
          setIsLogin(false)
        }
      }).catch((err)=>{alert(err.message)})
      
      
    setIsLoading(false)
    
     
    
  }

  let content=<button type='submit'>{isLogin ? 'Login' : 'Create an account'}</button>

  if(isLoading){
    content=<p>Sending Request...</p>
  }

  return (
    
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form  onSubmit={SubmitHandler}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' required ref={EmailRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input
            type='password'
            id='password'
            required
            ref={PasswordRef}
          />
        </div>
        {!isLogin && <div className={classes.control}>
          <label htmlFor='password'>Confirm Your Password</label>
          <input
            type='password'
            id='Confpassword'
            required
            ref={ConfPasswordRef}
          />
        </div>}
        <div className={classes.actions}>
          {content}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchLoginHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default Login;
