import React,{useState} from "react";
import {Container} from 'react-bootstrap'

const Profile=()=>{

    const [enteredName,setEnteredName]=useState('')
    const [enteredURL,setEnteredURL]=useState('')

    const NameChangeHandler=(event)=>{
        setEnteredName(event.target.value)
    }

    const URLChangeHandler=(event)=>{
        setEnteredURL(event.target.value)
    }

    const SubmitHandler=(event)=>{
        event.preventDefault()
        fetch('https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyC3xsAFKErtyTNybpuyKLlB9KUsc837SZY',{
            method: 'POST',
            body: {
                displayName: enteredName,
                imageUrl: enteredURL,
                returnSecureToken: true
            }
        }).then((res)=>{console.log('updated')}).catch((err)=>{alert('Something went wrong')})
    }

    return(
        <Container>
            <form onSubmit={SubmitHandler}>
            <header>
                <h2>Contact Details</h2>
            </header>
            <main>
                <label>Full Name:</label>
                <input type="text" value={enteredName} onChange={NameChangeHandler} />
                <br />
                <label>Profile Photo URL:</label>
                <input type="url" value={enteredURL} onChange={URLChangeHandler} />
            </main>
            <footer>
                <button type="submit">Update</button>
            </footer>
            </form>
        </Container>
    )
}

export default Profile
