import React, { useContext } from "react";
import { Link, useNavigate } from 'react-router-dom'
import classes from './MainNavigation.module.css'
import LoginContext from "../Login/Store/login-context";


const MainNavigation=()=>{

  const ctx=useContext(LoginContext)
  const navigate=useNavigate()

  let content;
  if(ctx.isLoggedin){
    content=<Link to='/Profile'>Profile</Link>       
  }
  else{
    content=<Link to='/'>Login</Link> 
  }

  const logoutHandler=()=>{
    ctx.logout();
    navigate('/')
  }

    return(
     <header className={classes.header}>
      <Link to='/'>
        <div className={classes.logo}>MyWebLink</div>
      </Link>
      <nav>
        <ul>
          <li>
            <Link to='/Home'>Home</Link>
          </li>
          <li>
            <Link to='/AddExpense'>AddExpense</Link>
          </li>
          <li>
            <Link to='/'>About Us</Link>
          </li>
          <li>
            {content}
          </li>
          <li>{ctx.isLoggedin && <button onClick={logoutHandler}>Logout</button>}</li>
        </ul>
      </nav>
      </header>
    )
}

export default MainNavigation
