import React from "react";
import { Card } from 'react-bootstrap'
import classes from './ExpenseDetails.module.css'

const ExpenseDetails= (props)=>{

    let content
    let des=props.expense.map(Des=>Des.description)
    let price= props.expense.map(Price=>Price.price)
    let category=props.expense.map(cat=>cat.category)



    try{
        for(let i=0;i<des.length;i++){
            const response=  fetch('https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses.json',{
            method: 'POST',
            body: JSON.stringify({
                description: des[i],
                price: price[i],
                category: category[i] 
            }),
            headers: {'Content-Type': 'application/json'}
        });
        console.log(response.ok)
        if(response.ok){
            alert('Expense has been updated')
        }
        else{
            throw new Error('Couldnt store the expense')
        }
        }
        const res=  fetch('https://react-http-333ab-default-rtdb.asia-southeast1.firebasedatabase.app/Expenses.json')
        if(!res.ok){
            throw new Error('Couldnt get data')
        }

        const data= res.json()
        console.log(data)
        let Details=[]
        for(const key of data){
            Details.push({
                description: data[key].description,
                price: data[key].price,
                category: data[key].category
            })
        }
        
        content = Details.map(details =>
            <li key={Math.random()}>
                {details.description} - Rs{details.price} - {details.category}
            </li>)
    }catch (err){
       alert(err.message)
    }
    return(
        <Card className={classes.users}>
        <ul>
            {content}
        </ul>
        </Card>
    )
}

export default ExpenseDetails
